mapper.select:
SELECT p.post_num, p.post_name, t.team_name, u.name, p.postType, date_format(p.postDate, "%Y %c/%e %r") as postDate FROM post AS p LEFT OUTER JOIN user AS u ON (p.uni_num = u.uni_num) INNER JOIN (SELECT u.uni_num, u.name, t.team_name FROM user AS u LEFT OUTER JOIN team AS t ON (u.team_id = t.team_id) WHERE 1 = 1
AND u.team_id = (SELECT team_id FROM user WHERE uni_num = #{uni_num})
AND u.team_id = (SELECT team_id FROM user WHERE uni_num = #{uni_num})
AND u.team_id = #{team_id}
) AS t ON (p.uni_num = t.uni_num) WHERE delYn = "N"
AND p.postType = #{postType}
ORDER BY 1 desc LIMIT ${index}, 5

SELECT count(*) tot FROM post as p where uni_num in (SELECT u.uni_num FROM user AS u inner JOIN team AS t ON (u.team_id = t.team_id) WHERE 1 = 1
AND u.team_id = (SELECT team_id FROM user WHERE uni_num = #{uni_num})
AND u.team_id = #{team_id}
) and p.delYn = "N"
AND p.postType = #{postType}
