mapper.select:
select uni_num, name
from user
where team_id != #{team_id}
order by uni_num
limit 0, 5;

select uni_num, name
from user
where name = #{name};

select uni_num, name
from user
where team_id = #{team_id}
and uni_num != #{uni_num};

mapper.update:
update user set
team_id = #{team_id}
where uni_num = #{uni_num};
